"use strict";

module.exports = int2hsla; // 1. some example message types:

var types = ['foo', 'bar', 'baz', 'info', 'data', 'fail', 'request', 'response']; // 2. generate colors and compare them:

for (var i = types.length; i--;) {
  var type = types[i]; // string

  var integer1 = str2hashint(type);
  var integer2 = str2hashint(type);
  var color1 = int2hsla(integer1);
  var color2 = int2hsla(integer2);
  var same = color1 === color2;
  if (same) console.log({
    color: color1
  });else console.error({
    color1: color1,
    color2: color2
  });
}

function int2hsla(i) {
  return "hsla(".concat(i % 360, ", 100%, 70%, 1)");
}

function str2hashint(str) {
  var hash = 0;
  var arr = str.split('');
  arr.forEach(function (v, i) {
    hash = str.charCodeAt(i) + ((hash << 5) - hash);
  });
  return hash;
}